import React from "react"
//import Box from "./Box.js"
import "./style.css"


class App extends React.Component {
    constructor() {
        super()
        this.state = {
            colors: ["white", "white", "white", "white"]
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick() {
        this.setState({ colors: ["black", "black", "white", "white"] })
    }


    render() {

        return (
            <div className="container">
                <p>{this.state.colors[1]}</p>
                {/* <Box color={this.state.colors[0]} />
                <Box color={this.state.colors[1]} />
                <Box color={this.state.colors[2]} />
                <Box color={this.state.colors[3]} /> */}
                <button onClick={this.handleClick}>Small Time</button>
            </div>
        )
    }
}

export default App
