import React, { useState } from "react"
import Box from "./Box.js"
import "./style.css"

function App() {
    const [color, setColor] = useState(["white", "white", "white", "white"])

    function handleClick(event) {
        event.preventDefault()
        if (color[0] === "white") {
            setColor(["black", "black", "black", "black"])
        } else { setColor(["white", "white", "white", "white"]) }
    }

    function handleClick2() {
        setColor(prevColor => ["purple", "purple", prevColor[2], prevColor[3]])
    }

    function handleClick3() {
        setColor(prevColor => [prevColor[0], prevColor[1], "blue", prevColor[3]])
    }

    function handleClick4() {
        setColor(prevColor => [prevColor[0], prevColor[1], prevColor[2], "blue"])
    }


    return (
        <div className="container">
            <Box color={color[0]} />
            <Box color={color[1]} />
            <Box color={color[2]} />
            <Box color={color[3]} />
            <button className="smallButton" onClick={handleClick}>Small Time</button>
            <button className="partyButton" onClick={handleClick2}>Party DJ</button>
            <button className="leftBlue" onClick={handleClick3}>Left Blue</button>
            <button className="rightBlue" onClick={handleClick4}>Right Blue</button>
        </div>
    )
}

export default App
