    //          return {
    //              colors: [prevState.colors[0], prevState.colors[1], prevState.colors[2], "blue"]
    //          }
    //      })
    //  }





    return (
        <div className="container">
            <Box color={color[0]} />
            <Box color={color[1]} />
            <Box color={color[2]} />
            <Box color={color[3]} />
            <button className="smallButton" onClick={handleClick}>Small Time</button>
            <button className="partyButton" onClick={handleClick2}>Party DJ</button>
            {/* <button className="leftBlue" onClick={handleClick3}>Left Blue</button>
            <button className="rightBlue" onClick={handleClick4}>Right Blue</button> */}
        </div>
    )
}

