import React, { useState, useEffect } from "react"

function App() {


    const [hitList, setHitList] = useState([])


    useEffect(() => {
        fetch("https://raw.githubusercontent.com/VSchool/vschool-api/master/static/hitlist.json")
            .then(resp => resp.json())
            .then(data => {
                setHitList([data])
            })
    }, [])
    console.log(hitList)

    const names = hitList.map((individual) => {
        return (
            <div>
                <p>{individual.name}</p>
                <img src={individual.image} alt="" width="500px" height="500px"></img>
            </div >
        )
    })



    return (
        <div>
            {names}
        </div>
    )
}

export default App

