import React, { useState, useEffect } from "react"



function Form() {
    const [coordinates, setCoordinates] = useState({ latitude: "", longitude: "" })
    const [xCoord, setXcoord] = useState("")
    const [yCoord, setYcoord] = useState("")
    const [temperature, setTemperature] = useState("")





    function handleChange(event) {
        const { name, value } = event.target
        setCoordinates(prevCoordinates => {
            return {
                ...prevCoordinates,
                [name]: value
            }

        })

    }



    // useEffect(() => {
    //     fetch(`https://api.weather.gov/points/${coordinates.latitude},${coordinates.longitude}`)
    //         .then(response => response.json())
    //         .then(json => setXcoord(json.properties.gridX))


    // }, [coordinates])

    // console.log(coordinates.latitude)




    // useEffect(() => {
    //     fetch(`https://api.weather.gov/points/${coordinates.latitude},${coordinates.longitude}`)
    //         .then(response => response.json())
    //         .then(json => setYcoord(json.properties.gridY))


    // }, [coordinates])

    useEffect(() => {
        fetch('https://api.weather.gov/points/38.8894,-77.0352')
            .then(response => response.json())
            .then(json => setYcoord(json.properties.gridY))
    }, [])

    useEffect(() => {
        fetch('https://api.weather.gov/points/38.8894,-77.0352')
            .then(response => response.json())
            .then(json => setXcoord(json.properties.gridX))
    }, [])



    useEffect(() => {
        fetch(`https://api.weather.gov/gridpoints/LWX/${yCoord},${xCoord}/forecast`)
            .then(response => response.json())
            .then(json => setTemperature(json.properties.periods[0].temperature))


    }, [xCoord, yCoord])

    console.log(coordinates.latitude)
    console.log(coordinates.longitude)
    console.log(yCoord)
    console.log(xCoord)


    function handleClick() {
        return (
            <div>Current Temperature: {temperature}</div>

        )
    }




    return (
        <form>
            Latitude: <input
                type="text"
                name="latitude"
                onChange={handleChange}
                value={coordinates.latitude}
            />
            Longitude: <input
                type="text"
                name="longitude"
                onChange={handleChange}
                value={coordinates.longitude}
            />
            <br></br>
            <button onClick={handleClick}>Submit</button>

            <div>Current Temperature: {temperature}</div>


        </form >
    )
}

export default Form
