import React, { useState, useEffect } from "react"



function Form() {
    const [coordinates, setCoordinates] = useState({ latitude: "", longitude: "" })
    const [xCoord, setXcoord] = useState("")
    const [yCoord, setYcoord] = useState("")
    const [temperature, setTemperature] = useState("")



    function handleClick(event) {
        event.preventDefault()
        useEffect(() => {
            fetch(`https://api.weather.gov/points/38.8894,-77.0352`)
                .then(response => response.json())
                .then(json => setXcoord(json.properties.gridX))


        }, [])

        useEffect(() => {
            fetch('https://api.weather.gov/points/38.8894,-77.0352')
                .then(response => response.json())
                .then(json => setYcoord(json.properties.gridY))


        }, [])

        useEffect(() => {
            fetch(`https://api.weather.gov/gridpoints/LWX/${xCoord},${yCoord}/forecast`)
                .then(response => response.json())
                .then(json => setTemperature(json.properties.periods[0].temperature))


        }, [xCoord, yCoord])

    }

    function handleChange(event) {
        const { name, value } = event.target
        setCoordinates(prevCoordinates => {
            return {
                ...prevCoordinates,
                [name]: value
            }

        })

    }
    console.log(coordinates.latitude)






    return (
        <form>
            Latitude: <input
                type="text"
                name="latitude"
                onChange={handleChange}
                value={coordinates.latitude}
            />
            Longitude: <input
                type="text"
                name="longitude"
                onChange={handleChange}
                value={coordinates.longitude}
            />
            <br></br>
            <button onClick={handleClick}>Submit</button>

            <div>Current Temperature: {temperature}</div>


        </form >
    )
}

export default Form
