import React, { useState, useEffect } from "react";
import axios from "axios";

const ThemeContext = React.createContext()

function ThemeContextProvider(props) {


    const [coordinates, setCoordinates] = useState({ latitude: "", longitude: "" })
    const [xCoord, setXcoord] = useState("")
    const [yCoord, setYcoord] = useState("")
    const [temperature, setTemperature] = useState("")
    const [shortForecast, setShortForecast] = useState("")
    const [windSpeed, setWindSpeed] = useState("")
    const [windDirection, setWindDirection] = useState("")

    function handleChange(event) {
        const { name, value } = event.target
        setCoordinates(prevCoordinates => {
            return {
                ...prevCoordinates,
                [name]: value
            }

        })

    }

    function handleClick(event) {
        event.preventDefault()
        axios.get(`https://api.weather.gov/points/${coordinates.latitude},${coordinates.longitude}`)
            .then(response => {
                setXcoord(response.data.properties.gridX)
                setYcoord(response.data.properties.gridY)
                console.log(xCoord, yCoord)
                coordinates.latitude = ""
                coordinates.longitude = ""

            })
            .catch(error => console.log(error))

    }

    useEffect(() => {
        axios.get(`https://api.weather.gov/gridpoints/LWX/${xCoord},${yCoord}/forecast`)
            // .then(response => console.log(response.data.properties.periods[0].temperature))
            .then(response => {
                setTemperature(response.data.properties.periods[0].temperature)
                setShortForecast(response.data.properties.periods[0].shortForecast)
                setWindSpeed(response.data.properties.periods[0].windSpeed)
                setWindDirection(response.data.properties.periods[0].windDirection)

            })
            .catch(error => console.log(error))
    }, [xCoord, yCoord])



    return (

        <ThemeContext.Provider value={{
            coordinates: coordinates.latitude,
            coordinates: coordinates.longitude,
            handleChange,
            handleClick,
            temperature,
            shortForecast,
            windDirection,
            windSpeed

        }}>
            {props.children}

        </ThemeContext.Provider>

    )
}

export { ThemeContext, ThemeContextProvider }
