import React, { useState, useEffect } from "react";
import axios from "axios"
import Bounties from "./components/Bounties.js"
import AddBountyForm from "./components/AddBountyForm.js";

export default function App() {
    const [bounty, setBounty] = useState([])

    function getBounty() {
        axios.get("/bounty")
            .then(res => setBounty(res.data))
            .catch(err => console.log(err))
    }

    function addBounty(newBounty) {
        axios.post("/bounty", newBounty)
            .then(res => {
                setBounty(prevBounties => [...prevBounties, res.data])
            })
    }

    function deleteMovie(bountyId) {
        axios.delete("/")
    }

    useEffect(() => {
        getBounty()
    }, [])

    useEffect(() => {
        axios.post("/bounty")
            .then(res => bounty.push(res.data))
    })
    return (
        <div>
            <AddBountyForm
                addBounty={addBounty}
            />
            {bounty.map(bounty => <Bounties {...bounty} key={bounty._id} />)}
        </div>
    )
}
