import AddItemForm from "./AddItemForm.js";

function Inventory(props) {
    const { name, price, _id } = props
    const [toggle, editToggle] = useState(false)
    return (
        <div>
            <h1>Item: {name}</h1>
            <p>Price: ${price}</p>
            <button onClick={() => props.deleteItem(_id)} >Delete</button>
            <AddItemForm
                name={name}
                price={price}
                btnText="submit edit"
            />
        </div>
    )
}

export default Inventory
