import React from "react"

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            placeOfBirth: "",
            phoneNumber: "",
            favFood: "",
            infoBox: "",
            badgeList: []



        }
        this.handleChange = this.handleChange.bind(this)
        // this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleChange(event) {
        const { firstName, lastName, email, placeOfBirth, phoneNumber, favFood, infoBox, value } = event.target
        this.setState(() => {
            return {
                [firstName]: value,
                [lastName]: value,
                [email]: value,
                [placeOfBirth]: value,
                [phoneNumber]: value,
                [favFood]: value,
                [infoBox]: value


            }
        })
    }

    // handleSubmit(event) {
    //     event.preventDefault()
    //     this.setState(prevState => {
    //         return {
    //             firstName: "",
    //             lastName: "",
    //             email: "",
    //             placeOfBirth: "",
    //             phoneNumber: "",
    //             favFood: "",
    //             infoBox: "",
    //             badgeList: [...prevState.badgeList, this.state.firstName]
    //         }
    //     })
    // }

    render() {

        const completeBadge = this.state.badgeList.map(function (item) {
            return (
                <p>{item}</p>
            )
        })

        return (
            <main>
                <form>
                    <input
                        name="firstName"
                        type="text"
                        value={this.state.firstName}
                        placeholder="First Name"
                        onChange={this.handleChange}
                        minLength="3"
                    />

                    <input
                        name="lastName"
                        type="text"
                        value={this.state.lastName}
                        placeholder="Last Name"
                        onChange={this.handleChange}
                        minLength="3"

                    />

                    <input
                        name="email"
                        type="text"
                        value={this.state.email}
                        placeholder="Email Address"
                        onChange={this.handleChange}
                        minLength="3"

                    />

                    <input
                        name="placeOfBirth"
                        type="text"
                        value={this.state.placeOfBirth}
                        placeholder="Place of Birth"
                        onChange={this.handleChange}
                        minLength="3"

                    />

                    <input
                        name="phoneNumber"
                        type="text"
                        value={this.state.phoneNumber}
                        placeholder="Phone Number"
                        onChange={this.handleChange}
                    />

                    <input
                        name="favFood"
                        type="text"
                        value={this.state.favFood}
                        placeholder="Favorite Food"
                        onChange={this.handleChange}
                        minLength="3"

                    />

                    <input
                        name="infoBox"
                        type="text"
                        value={this.state.infoBox}
                        placeholder="Tell us about yourself"
                        onChange={this.handleChange}
                        minLength="3"

                    />

                    <button onClick={this.handleSubmit}>Submit</button>

                    <div>{completeBadge}</div>
                </form>

            </main>
        )
    }
}

export default App
