import React, { useState } from "react"
import Badge from "./Badge"

function App() {

    const [inputData, setInputData] = useState({ firstName: "", lastName: "", email: "", placeOfBirth: "", phoneNumber: "", favFood: "", infoBox: "" })
    const [badgeList, setBadgeList] = useState([])

    function handleChange(event) {
        const { name, value } = event.target

        setInputData(prevInputData => ({ ...prevInputData, [name]: value }))
    }

    function handleSubmit(event) {
        event.preventDefault()
        setBadgeList(prevBadgeData => [...prevBadgeData, (inputData.firstName: ""),

            <Badge
                firstName={inputData.firstName}
                lastName={inputData.lastName}
                email={inputData.email}
                placeOfBirth={inputData.placeOfBirth}
                phoneNumber={inputData.phoneNumber}
                favFood={inputData.favFood}
                infoBox={inputData.infoBox}
            />
        ]
        )
    }


    function onHandleTelephoneChange(event) {
        let phoneNumber = event.target.value

        if (!Number(phoneNumber)) {
            return;
        }
        setInputData({
            [event.target.name]: phoneNumber
        })
    }

    const newBadge = badgeList.map(function (item) {
        return (

            item
        )
    })


    return (
        <main>
            <form onSubmit={handleSubmit} id="form">
                <input
                    name="firstName"
                    id="firstName"
                    type="text"
                    value={inputData.firstName}
                    placeholder="First Name"
                    onChange={handleChange}
                    required minLength={3}


                />

                <input
                    name="lastName"
                    id="lastName"
                    type="text"
                    value={inputData.lastName}
                    placeholder="Last Name"
                    onChange={handleChange}
                    required minLength={3}


                />

                <input
                    name="email"
                    id="email"
                    type="text"
                    value={inputData.email}
                    placeholder="Email Address"
                    onChange={handleChange}
                    required minLength={3}

                />

                <input
                    name="placeOfBirth"
                    id="placeOfBirth"
                    type="text"
                    value={inputData.placeOfBirth}
                    placeholder="Place of Birth"
                    onChange={handleChange}
                    required minLength={3}


                />

                <input
                    name="phoneNumber"
                    id="phoneNumber"
                    type="tel"
                    value={inputData.phoneNumber}
                    placeholder="Phone Number"
                    onChange={onHandleTelephoneChange}
                    required minLength={3}
                />

                <input
                    name="favFood"
                    id="favFood"
                    type="text"
                    value={inputData.favFood}
                    placeholder="Favorite Food"
                    onChange={handleChange}
                    required minLength={3}


                />

                <input
                    name="infoBox"
                    id="infoBox"
                    type="text"
                    value={inputData.infoBox}
                    placeholder="Tell us about yourself"
                    onChange={handleChange}
                    required minLength={3}


                />

                <button id="button" onClick={handleSubmit}>Submit</button>
            </form>


            <div>{newBadge}</div>




        </main>
    )
}

export default App


