import React from "react"

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            placeOfBirth: "",
            phoneNumber: 0,
            favFood: "",
            infoBox: "",
            badgeList: []



        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleChange(event) {
        const { name, value } = event.target
        this.setState({ [name]: value })
    }

    handleSubmit(event) {
        event.preventDefault()
        this.setState(prevState => {
            return {
                firstName: "",
                lastName: "",
                email: "",
                placeOfBirth: "",
                phoneNumber: "",
                favFood: "",
                infoBox: "",
                badgeList: [
                    ...prevState.badgeList,
                    this.state.firstName,
                    this.state.lastName,
                    this.state.email,
                    this.state.phoneNumber,
                    this.state.placeOfBirth,
                    this.state.favFood,
                    this.state.infoBox
                ]
            }
        })
    }

    render() {

        // const completeBadge = this.state.badgeList.map(function (item) {
        //     return (
        //         <li>{item}</li>
        //     )
        // })

        return (
            <main>
                <form>
                    <input
                        name="firstName"
                        type="text"
                        value={this.state.firstName}
                        placeholder="First Name"
                        onChange={this.handleChange}
                        minLength="3"
                        required
                    />

                    <input
                        name="lastName"
                        type="text"
                        value={this.state.lastName}
                        placeholder="Last Name"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="email"
                        type="text"
                        value={this.state.email}
                        placeholder="Email Address"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="placeOfBirth"
                        type="text"
                        value={this.state.placeOfBirth}
                        placeholder="Place of Birth"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="phoneNumber"
                        type="number"
                        value={this.state.phoneNumber}
                        placeholder="Phone Number"
                        onChange={this.handleChange}
                        required
                    />

                    <input
                        name="favFood"
                        type="text"
                        value={this.state.favFood}
                        placeholder="Favorite Food"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="infoBox"
                        type="text"
                        value={this.state.infoBox}
                        placeholder="Tell us about yourself"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />




                    <button onClick={this.handleSubmit}>Submit</button>

                    {/* <ul>
                        {completeBadge}
                    </ul> */}

                </form>

                <p>Name : {this.state.firstName} {this.state.lastName}</p>
                <p>placeOfBirth: {this.state.placeOfBirth}</p>
                <p>{this.state.email}</p>
                <p>{this.state.phoneNumber}</p>
                <p>{this.state.favFood}</p>
                <p>{this.state.infoBox}</p>



            </main>
        )
    }
}

export default App
