import React from "react"
import NewBadge from "./NewBadge"

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            placeOfBirth: "",
            phoneNumber: "",
            favFood: "",
            infoBox: "",
            badgeList: []



        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleChange(event) {
        const { name, value } = event.target
        this.setState({ [name]: value })
    }

    handleSubmit(event) {
        event.preventDefault()
        this.setState(prevState => {



            return {

                firstName: "",
                lastName: "",
                email: "",
                placeOfBirth: "",
                phoneNumber: "",
                favFood: "",
                infoBox: "",
                badgeList: [
                    ...prevState.badgeList,
                    this.state.firstName,
                    this.state.lastName,
                    this.state.email,
                    this.state.phoneNumber,
                    this.state.placeOfBirth,
                    this.state.favFood,
                    this.state.infoBox
                ]

            }
        })

    }

    // const completeBadge = this.state.badgeList.map(function (item) {
    //     return (
    //         <ul>
    //             <NewBadge data={item} />
    //         </ul>
    //     )
    // })


    render() {



        return (
            <main>
                <form id="form">
                    <input
                        name="firstName"
                        type="text"
                        value={this.state.firstName}
                        placeholder="First Name"
                        onChange={this.handleChange}
                        minLength="3"
                        required
                    />

                    <input
                        name="lastName"
                        type="text"
                        value={this.state.lastName}
                        placeholder="Last Name"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="email"
                        type="text"
                        value={this.state.email}
                        placeholder="Email Address"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="placeOfBirth"
                        type="text"
                        value={this.state.placeOfBirth}
                        placeholder="Place of Birth"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="phoneNumber"
                        type="number"
                        value={this.state.phoneNumber}
                        placeholder="Phone Number"
                        onChange={this.handleChange}
                        required
                    />

                    <input
                        name="favFood"
                        type="text"
                        value={this.state.favFood}
                        placeholder="Favorite Food"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />

                    <input
                        name="infoBox"
                        type="text"
                        value={this.state.infoBox}
                        placeholder="Tell us about yourself"
                        onChange={this.handleChange}
                        minLength="3"
                        required

                    />




                    <button onClick={this.handleSubmit}>Submit</button>



                </form>

                <div>
                    <NewBadge name={this.state.firstName} />
                </div>

                {/* <p>Name : {this.state.firstName} {this.state.lastName}</p>
                <p>Place Of Birth: {this.state.placeOfBirth}</p>
                <p>Email: {this.state.email}</p>
                <p>Phone Number: {this.state.phoneNumber}</p>
                <p>Favorite Foods: {this.state.favFood}</p>
                <p>{this.state.infoBox}</p> */}



            </main>
        )
    }
}

export default App
