import React from "react"

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            name: ""
        }
        this.handleClick = this.handleClick.bind(this)
    }

    handleClick(event) {
        const { name, value } = event.target
        this.setState({
            [name]: value
        })
    }

    render() {
        return (
            <main>
                <form>
                    <input name="name" value={this.state.name} onChange={this.handleClick} placeholder="Name" />
                    <h1>{this.state.name}</h1>
                    <button onClick={this.handleClick}>Submit</button>
                    <ol></ol>
                </form >
            </main>
        )
    }
}


export default App
