import React, { useState, useEffect } from "react"
import Form from "./Form"


function App() {

    const [uglyList, setUglyList] = useState([])

    useEffect(() => {
        fetch("https://api.vschool.io/chrismiller/thing")
            .then(response => response.json())
            .then(data => {
                setUglyList(data)
            })
    }, [])

    const individualThing = uglyList.map((thing) => {
        return (
            <div>
                <p>{thing.title}</p>
                <img src={thing.image}></img>
                <p>{thing.description}</p>
            </div>
        )
    })

    return (
        <div>
            <Form />
        </div>
    )
}

export default App
