import React from "react"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {


    function UglyListAsIs() {
        fetch("https://api.vschool.io/chrismiller/thing")
            .then(response => response.json())
            .then(data => {
                setListData(data)
            })
    }



    function newUglyThing(uglyThing) {
        axios.post("https://api.vschool.io/chrismiller/thing", uglyThing)


    }

    // // function handleDelete(id) { 
    //     axios.delete("https://api.vschool.io/chrismiller/thing/{id}")
    // }

    // function handleEdit() { }





    return (
        <ThemeContext.Provider value={{ UglyListAsIs, newUglyThing }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

