                        onChange={context.handleEditChange}
                        placeholder="Enter a New Image Url"
                    />
                    <br />
                    <button onClick={((e) => {
                        e.preventDefault()
                        context.handleEdit(context.uglyThing.id)
                    })}>Submit Changes</button>
                    <button onClick={context.cancelEdit}>Cancel Changes</button>
                </form>
            </div>
        </div>
    )
}
