import React, { useContext } from "react"
import { ThemeContext } from "./components/themeContext"

function Form(props) {

    const context = useContext(ThemeContext)


    return (
        <main className="form">
            <form>
                <input
                    name="title"
                    // value={inputData.title}
                    placeholder="Title"
                    onChange={context.handleChange}
                >

                </input>

                <input
                    name="imgurl"
                    // value={inputData.imgurl}
                    // alt={inputData.title}
                    placeholder="Img Url"
                    onChange={context.handleChange}

                >

                </input>

                <input
                    name="description"
                    // value={inputData.description}
                    placeholder="Description"
                    onChange={context.handleChange}
                >
                </input>

                <button onClick={context.handleSubmit}>Add Ugly Thing</button>

            </form>
        </main >
    )
}

export default Form
