import axios from "axios"
import React, { useState, useEffect } from "react"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {




    useEffect(() => {
        getData()
    }, [])

    function getData(id) {

        axios.get("https://api.vschool.io/chrismiller86/thing/")
            .then(res => { setUglyList(res.data) })
            .catch(error => console.log(error))
    }

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", inputData)
            .then(() => getData())
            .catch(error => console.log(error))
        setInputData(blankInfo)
    }

    function handleDelete(id) {

        axios.delete(`https://api.vschool.io/chrismiller86/thing/${id}`)
            .then(() => setUglyList(prevThing => {
                return prevThing.filter(thing => thing._id !== id)
            }))
            .catch(error => console.log(error))
    }

    function handleEdit(e, id) {
        axios.put(`https://api.vschool.io/chrismiller86/thing/${id}`, inputData)
            .then(response => {
                setUglyList(prevUglyList => {
                    return prevUglyList.map(thing => thing._id === id ? response.data : thing)
                })
                getData()

            })
            .catch(error => console.log(error))

    }

    function toggleEdit() {
        setInputData(prevState => ({ isEditing: !prevState.isEditing }))
    }






    return (
        <ThemeContext.Provider value={{
            inputData,
            uglyList,
            setUglyList,
            handleChange,
            handleSubmit,
            handleDelete,
            handleEdit,
            toggleEdit


        }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

export { ThemeContextProvider, ThemeContext }
