import React, { useContext } from "react"
import { ThemeContext } from "../themeContext"
import UglyItem from "./UglyItem"
import axios from "axios"

function UglyList(props) {

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", inputData)
            .then(() => props.getData())
            .catch(error => console.log(error))
        setInputData(blankInfo)
    }

    function handleChange(event) {
        const { name, value } = event.target
        props.setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }

    function toggleEdit() {
        setInputData(prevState => ({ isEditing: !prevState.isEditing }))
    }

    function handleEdit(e, id) {
        axios.put(`https://api.vschool.io/chrismiller86/thing/${id}`, inputData)
            .then(response => {
                setUglyList(prevUglyList => {
                    return prevUglyList.map(thing => thing._id === id ? response.data : thing)
                })
                getData()

            })
            .catch(error => console.log(error))

    }




    const context = useContext(ThemeContext)
    const list = context.uglyList.map((item, index) =>
    ((
        console.log(item._id),
        < UglyItem
            key={index}
            id={item._id}
            title={item.title}
            img={item.imgUrl}
            description={item.description}
            handleIt={handleSubmit}
            changeIt={handleChange}
            toggleIt={toggleEdit}
            editIt={handleEdit}

        />
    ))
    )

    return (
        <div>
            {list}
        </div>
    )
}

export default UglyList
