import axios from "axios"
import React, { useState, useEffect } from "react"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {

    const { uglyList, setUglyList } = useState([])




    useEffect(() => {
        getData()
    }, [])

    function getData(id) {

        axios.get("https://api.vschool.io/chrismiller86/thing/")
            .then(res => { setUglyList(res.data) })
            .catch(error => console.log(error))
    }

    function handleEdit(e, id) {
        axios.put(`https://api.vschool.io/chrismiller86/thing/${id}`, inputData)
            .then(response => {
                context.setUglyList(prevUglyList => {
                    return prevUglyList.map(thing => thing._id === id ? response.data : thing)
                })
                context.getData()

            })
            .catch(error => console.log(error))

    }

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }



    function handleDelete(id) {

        axios.delete(`https://api.vschool.io/chrismiller86/thing/${id}`)
            .then(() => setUglyList(prevThing => {
                return prevThing.filter(thing => thing._id !== id)
            }))
            .catch(error => console.log(error))
    }








    return (
        <ThemeContext.Provider value={{

            uglyList,
            setUglyList,
            handleDelete,
            getData



        }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

export { ThemeContextProvider, ThemeContext }
