import React, { useContext } from "react"
import { ThemeContext } from "../themeContext"
import UglyItem from "./UglyItem"
import axios from "axios"

function UglyList(props) {

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", props.inputData)
            .then(() => props.getData())
            .catch(error => console.log(error))
        props.setInputData(props.blankInfo)
    }

    function handleChange(event) {
        const { name, value } = event.target
        props.setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }


    const context = useContext(ThemeContext)
    const list = context.uglyList.map((item, index) =>
    ((
        console.log(item._id),
        < UglyItem
            key={index}
            id={item._id}
            title={item.title}
            img={item.imgUrl}
            description={item.description}
            handleIt={handleSubmit}
            changeIt={handleChange}

        />
    ))
    )

    return (
        <div>
            {list}
        </div>
    )
}

export default UglyList
