import axios from "axios"
import React, { useState, useEffect } from "react"
import UglyItem from "./components/UglyItem"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {

    const blankInfo = {
        title: "",
        imgUrl: props.item?.title || "",
        description: ""
    }

    const [inputData, setInputData] = useState(blankInfo)
    const [uglyList, setUglyList] = useState([])


    useEffect(() => {
        getData()
    }, [])

    function getData(id) {
        console.log(id)

        axios.get("https://api.vschool.io/chrismiller86/thing/")
            .then(res => { setUglyList(res.data) })
            .catch(error => console.log(error))
    }

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", inputData)
            .then(() => getData())
            .catch(error => console.log(error))
        setInputData(blankInfo)
    }

    function handleDelete(id) {
        axios.delete("https://api.vschool.io/chrismiller/thing/{id}")
            .then(() => setUglyList(prevThing => {
                return prevThing.filter(thing => thing._id !== id)
            }))
            .catch(error => console.log(error))
    }


    // function UglyListAsIs() {
    //     fetch("https://api.vschool.io/chrismiller/thing")
    //         .then(response => response.json())
    //         .then(data => {
    //             setListData(data)
    //         })
    // }






    // function handleEdit() { }





    return (
        <ThemeContext.Provider value={{
            inputData,
            uglyList,
            setUglyList,
            handleChange,
            handleSubmit,
            handleDelete

        }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

export { ThemeContextProvider, ThemeContext }
