import React, { useState, useEffect } from "react"
import axios from "axios"

function UglyList() {

    const [uglyList, setUglyList] = useState([])

    useEffect(() => {
        axios.get("https://api.vschool.io/chrismiller/thing")
            .then(response => response.json())
            .then(data => {
                setUglyList(data)
            })
    }, [])

    const individualThing = uglyList.map((thing) => {
        return (
            <div>
                <p>{thing.title}</p>
                <img src={thing.image}></img>
                <p>{thing.description}</p>
            </div>
        )
    })

    return (

        <div>
            {individualThing}
        </div>

    )
}

export default UglyList
