import React, { useContext, useState } from "react"
import { ThemeContext } from "../themeContext"
import axios from "axios"
import Form from "./Form"


function UglyItem(props) {

    const blankInfo = {
        title: "",
        imgUrl: props.item?.title || "",
        description: "",
        isEditing: false
    }

    const [inputData, setInputData] = useState(blankInfo)


    const context = useContext(ThemeContext)

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", inputData)
            .then(() => context.getData())
            .catch(error => console.log(error))
        setInputData(blankInfo)
    }

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }

    function toggleEdit() {
        setInputData(prevState => ({ isEditing: !prevState.isEditing }))
    }

    function handleEdit(e, id) {
        axios.put(`https://api.vschool.io/chrismiller86/thing/${id}`, inputData)
            .then(response => {
                context.setUglyList(prevUglyList => {
                    return prevUglyList.map(thing => thing._id === id ? response.data : thing)
                })
                context.getData()

            })
            .catch(error => console.log(error))

    }






    return (
        <>
            <Form
                Submit={handleSubmit}
                edit={handleEdit}
            />
            {inputData.isEditing === false ?
                <>
                    <div>
                        <h3>{props.title}</h3>
                        <img src={props.img} alt={props.description}></img>
                        <p>{props.description}</p>
                        <h3>{props.id}</h3>
                    </div>

                    <button onClick={() => context.handleDelete(props.id)}>Delete</button>
                    <button onClick={toggleEdit}>Edit</button>
                </>

                :

                <div>

                    <form>
                        <input
                            name="editTitle"
                            placeholder="New title"
                            onChange={handleChange}
                        ></input>
                        <input
                            name="editImageUrl"
                            placeholder="New image Url"
                            onChange={handleChange}
                        ></input>
                        <input
                            name="editDescription"
                            placeholder="New description"
                            onChange={handleChange}
                        ></input>
                        <button onClick={handleEdit(inputData.id)}>Submit Edit</button>


                    </form>
                </div>
            }
        </>

    )
}

export default UglyItem
