import axios from "axios"
import React, { useState, useEffect } from "react"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {

    const blankInfo = {
        title: "",
        imgUrl: props.item?.title || "",
        description: ""
    }

    const [inputData, setInputData] = useState(blankInfo)
    const [uglyList, setUglyList] = useState([])


    useEffect(() => {
        getData()
    }, [])

    function getData(id) {

        axios.get("https://api.vschool.io/chrismiller86/thing/")
            .then(res => { setUglyList(res.data) })
            .catch(error => console.log(error))
    }

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }

    function handleSubmit(event) {
        event.preventDefault()

        axios.post("https://api.vschool.io/chrismiller86/thing", inputData)
            .then(() => getData())
            .catch(error => console.log(error))
        setInputData(blankInfo)
    }

    function handleDelete(id) {

        axios.delete(`https://api.vschool.io/chrismiller86/thing/${id}`)
            .then(() => setUglyList(prevThing => {
                return prevThing.filter(thing => thing._id !== id)
            }))
            .catch(error => console.log(error))
    }

    function handleEdit(id) {
        axios.put(`https://api.vschool.io/chrismiller86/thing/${id}`)
            .then(() => getData())
            .catch(error => console.log(error))

    }

    function EditForm() {

        const context = useContext(ThemeContext)

        return (

            <>
                <form onSubmit={context.handleEdit}>
                    <input
                        name="editTitle"
                    // onChange={ }
                    >New Title</input>
                    <input
                        name="editImageUrl"
                    // onChange={ }
                    >New ImageUrl</input>
                    <input
                        name="editDescription"
                    // onChange={ }
                    >New Description</input>
                    <button>Submit Edit</button>

                </form>
            </>
        )
    }




    return (
        <ThemeContext.Provider value={{
            inputData,
            uglyList,
            setUglyList,
            handleChange,
            handleSubmit,
            handleDelete,
            handleEdit,
            EditForm

        }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

export { ThemeContextProvider, ThemeContext }
