import React, { useContext } from "react"
import { ThemeContext } from "../themeContext"


function Form(props) {

    const { inputData, setInputData, newUglyThing } = useContext(ThemeContext)

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => {
            return {
                ...prevInputData,
                [name]: value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDefault()
        newUglyThing(setInputData)
    }


    return (
        <main className="form">
            <form>
                <input
                    type="text"
                    name="title"
                    value={inputData.title}
                    placeholder="Title"
                    onChange={handleChange}
                >

                </input>

                <input
                    type="text"
                    name="imgurl"
                    value={inputData.imgurl}
                    alt={inputData.title}
                    placeholder="Img Url"
                    onChange={handleChange}

                >

                </input>

                <input
                    type="text"
                    name="description"
                    value={inputData.description}
                    placeholder="Description"
                    onChange={handleChange}
                >
                </input>

                <button onClick={handleSubmit}>Add Ugly Thing</button>

            </form>
        </main >
    )
}

export default Form
