import React, { useContext, useState } from "react"
import { ThemeContext } from "../themeContext"


function UglyItem(props) {

    const blankInfo = {
        title: "",
        imgUrl: props.item?.title || "",
        description: "",
        isEditing: false
    }

    const [inputData, setInputData] = useState(blankInfo)


    const context = useContext(ThemeContext)

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => ({

            ...prevInputData,
            [name]: value

        }
        ))
    }



    function toggleIt() {
        context.toggleEdit()
    }


    return (
        <>
            {props.isEditing === false ?
                <>
                    <div>
                        <h3>{props.title}</h3>
                        <img src={props.img} alt={props.description}></img>
                        <p>{props.description}</p>
                        <h3>{props.id}</h3>
                    </div>

                    <button onClick={() => context.handleDelete(props.id)}>Delete</button>
                    <button onClick={toggleIt}>Edit</button>
                </>

                :

                <div>

                    <form>
                        <input
                            name="editTitle"
                            placeholder="New title"
                            onChange={context.handleChange}
                        ></input>
                        <input
                            name="editImageUrl"
                            placeholder="New image Url"
                            onChange={context.handleChange}
                        ></input>
                        <input
                            name="editDescription"
                            placeholder="New description"
                            onChange={context.handleChange}
                        ></input>
                        <button onClick={context.handleEdit(inputData.id)}>Submit Edit</button>


                    </form>
                </div>
            }
        </>

    )
}

export default UglyItem
