import React, { useContext } from "react"
import { ThemeContext } from "../themeContext"


function Form(props) {

    const { handleChange, handleSubmit, inputData } = useContext(ThemeContext)



    return (
        <main className="form">
            <form>
                <input
                    type="text"
                    name="title"
                    value={inputData.title}
                    placeholder="Title"
                    onChange={handleChange}
                >

                </input>

                <input
                    type="text"
                    name="imgurl"
                    value={inputData.imgurl}
                    alt={inputData.title}
                    placeholder="Img Url"
                    onChange={handleChange}

                >

                </input>

                <input
                    type="text"
                    name="description"
                    value={inputData.description}
                    placeholder="Description"
                    onChange={handleChange}
                >
                </input>

                <button onClick={handleSubmit}>Add Ugly Thing</button>

            </form>
        </main >
    )
}

export default Form
