import axios from "axios"
import React, { useState, useEffect } from "react"
const ThemeContext = React.createContext()

function ThemeContextProvider(props) {
    const { inputData, setInputData } = useState({ title: "", imgurl: "", description: "" })

    const { listData, setListData } = useState([])

    // useEffect(() => {
    //     fetch("https://api.vschool.io/chrismiller/thing")
    //         .then(response => response.json())
    //         .then(data => {
    //             setListData(data)
    //         })
    // }, [])

    function handleChange(event) {
        const { name, value } = event.target
        setInputData(prevInputData => {
            return {
                ...prevInputData,
                [name]: value
            }
        })
    }

    function handleSubmit(event) {
        event.preventDefault()
        axios.post("https://api.vschool.io/chrismiller/thing")


    }

    // function handleEdit() { }

    // function handleDelete() { }



    return (
        <ThemeContext.Provider value={{ title: inputData.title, imgurl: inputData.imgurl, description: inputData.description, handleChange, handleSubmit }}>
            {props.children}
        </ThemeContext.Provider >
    )
}

export { ThemeContextProvider, ThemeContext }
