import React, { useEffect, useState } from "react"
import axios from "axios"
const UglyContext = React.createContext()

function UglyContextProvider(props) {

    const [uglyFormData, setUglyFormData] = useState({ title: "", imgUrl: "", description: "" })
    const [uglyList, setUglyList] = useState([])

    useEffect(() => {
        getList()
    }, [])

    function getList(id) {
        axios.get("https://api.vschool.io/chris-miller/thing")
            .then(response => { setUglyList(response.data) })
            .catch(error => console.log(error))

    }

    function handleChange(event) {
        const { name, value } = event.target
        setUglyFormData(prevFormData => ({ prevFormData, [name]: value }))

    }

    function handleSubmit(event) {
        event.preventDefault()
        axios.post("https://api.vschool.io/chris-miller/thing", uglyFormData)
            .then(() => getList())
            .catch(error => console.log(error))
        // setUglyFormData(uglyFormData)

    }

    function handleDelete(id) {
        axios.delete(`https://api.vschool.io/chris-miller/thing/${id}`)
            .then(() => setUglyList(prevThing => {
                return prevThing.filter(thing => thing._id !== id)
            }))
            .catch(error => console.log(error))

    }

    return (
        <UglyContext.Provider value={{
            handleChange,
            handleSubmit,
            handleDelete,
            uglyFormData,
            uglyList,
            setUglyList
        }}>
            {props.children}
        </UglyContext.Provider>

    )

}

export { UglyContextProvider, UglyContext }
