import React, { useContext } from "react"
import UglyItem from "./UglyItem"
import { UglyContext } from "./UglyContext"

function UglyList() {

    const uglyContext = useContext(UglyContext)

    const list = uglyContext.uglyList.map((item, index) => ((
        <UglyItem
            key={index}
            id={item._id}
            title={item.title}
            img={item.imgUrl}
            description={item.description}
        />
    )))

    return (
        <>
            {list}
        </>


    )
}

export default UglyList
