import React from "react"
import { ListContext } from "./UglyContext"
import UglyItem from "./UglyItem"
import axios from "axios"

function UglyList() {

    const { thingsArray, handleDelte, handleEditSave } = React.useContext(ListContext)

    const thingsListElement = thingsArray.map((thing, index) => {
        return <UglyItem key={thing.id} {...thing} index={index} onSave={() => handleEditSave(index)} onDelete={() => handleDelte(index)} />

    })

    return (
        <>
            {thingsListElement}
        </>
    )
}



export default UglyList
